tinymce.PluginManager.add('file_manager', function(editor, url) {
    editor.addButton('file_manager_button', {
        text: false,
        tooltip: 'File manager',
        icon: 'browse',
        onclick: open_aux_file_manager
    });

    function open_aux_file_manager() {

        win = editor.windowManager.open({
            title: 'File Manager',
            file: '/js/tinymce-4.6.1/plugins/file_manager/file_manager.php',
            width: 800,
            height: 570,
            inline: 1,
            resizable: true,
            maximizable: true
        });
    }
});





